import React, { useState, useEffect } from "react";
import gif from "../images/logo.gif";
import farmer from "../images/farmer.png";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "../component/Header";
import Footer from "../component/Footer";
import WhySell from "../component/WhySell";

function FarmerRegister() {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [locations, setLocations] = useState([]);
  const [values, setValues] = useState({
    name: "",
    email: "",
    password: "",
    phone_number: "",
    location: "",
  });

  // Function to detect window size and adjust the layout for mobile or desktop
  const handleResize = () => {
    setIsMobile(window.innerWidth < 768);
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  // Fetch locations from the backend
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await axios.get("http://localhost:3000/locations"); // Adjust endpoint as necessary
        setLocations(response.data); // Assuming response.data is an array of location objects
      } catch (error) {
        console.error("Error fetching locations:", error);
      }
    };
    fetchLocations();
  }, []);

  const handleChanges = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const navigate = useNavigate();

  const handleSumbit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:3000/auth/farmerregister", // Changed endpoint for farmer registration
        values
      );
      console.log(response);
      if (response.status === 201) {
        navigate("/farmerlogin"); // Redirect to the farmer login page
      }
    } catch (err) {
      console.log(err.message);
    }
  };

  return (
    <>
      <Header />

      <div className="flex-1 items-center">
        <div
          className="flex justify-center items-center p-4 flex-col md:flex-row py-9"
          style={{ backgroundColor: "green" }}
        >
          <div style={{ width: "50%", paddingRight: "20px" }}>
            <h1 style={{ fontSize: isMobile ? "30px" : "48px", fontWeight: "bold" }}>
              Become A Kaca Bazar Farmer Today!
            </h1>
            <p style={{ fontSize: "20px", marginTop: "20px" }}>
              Create a Kaca Bazar farmer account now and reach millions of customers!
            </p>
            <img
              src={farmer}
              alt="Farmer"
              style={{ marginTop: "50px", width: "250px" }}
            />
          </div>

          {/* Form Section */}
          <div
            className="shadow-lg px-8 py-5 border w-full max-w-lg md:max-w-3xl"
            style={{ backgroundColor: "white", borderRadius: "20px" }}
          >
            <div className="flex flex-col md:flex-row justify-center items-center">
              <img
                style={{ width: "300px", height: "auto" }}
                src={gif}
                alt="Animated GIF"
                className="mb-4 md:mb-0 md:mr-4 w-full md:w-1/2"
              />
              <div className="flex justify-center items-center flex-col w-full md:w-1/2">
                <h2 className="text-lg font-bold mb-4">Register</h2>
                <form className="w-full" onSubmit={handleSumbit}>
                  <div className="mb-4">
                    <label htmlFor="name" className="block text-gray-700">Name</label>
                    <input
                      type="text"
                      placeholder="Enter Name"
                      className="w-full px-3 py-2 border"
                      name="name"
                      onChange={handleChanges}
                    />
                  </div>
                  <div className="mb-4">
                    <label htmlFor="email" className="block text-gray-700">Email</label>
                    <input
                      type="email"
                      placeholder="Enter Email"
                      className="w-full px-3 py-2 border"
                      name="email"
                      onChange={handleChanges}
                    />
                  </div>
                  <div className="mb-4">
                    <label htmlFor="password" className="block text-gray-700">Password</label>
                    <input
                      type="password"
                      placeholder="Enter Password"
                      className="w-full px-3 py-2 border"
                      name="password"
                      onChange={handleChanges}
                    />
                  </div>
                  <div className="mb-4">
                    <label htmlFor="phone_number" className="block text-gray-700">Phone Number</label>
                    <input
                      type="text"
                      placeholder="Enter Phone Number"
                      className="w-full px-3 py-2 border"
                      name="phone_number"
                      onChange={handleChanges}
                    />
                  </div>
                  {/* Location Dropdown */}
                  <div className="mb-4">
                    <label htmlFor="location" className="block text-gray-700">Location</label>
                    <select
                      name="location"
                      className="w-full px-3 py-2 border"
                      onChange={handleChanges}
                    >
                      <option value="">Select Location</option>
                      {locations.map(location => (
                        <option key={location.Location_ID} value={location.Location_ID}>
                          {location.Location_Name} {/* Adjust as per your actual location property */}
                        </option>
                      ))}
                    </select>
                  </div>
                  
                  

                  <button className="w-full bg-green-600 text-white py-2">
                    Submit
                  </button>
                </form>
                <div className="text-center mt-4">
                  <span>Already have a farmer account?</span>
                  <Link to="/farmerlogin" className="text-blue-500">Login</Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <WhySell />
      <Footer />
    </>
  );
}

export default FarmerRegister;
