import { useState, useEffect } from "react";
import logo from "../images/KacaBazarLogo.png";
import { Link, useNavigate } from "react-router-dom";
import seller from "../images/agent.png";
import farmer from "../images/farmerlogo.png";
import axios from 'axios'; // Make sure to import axios

export default function Header() {
  const [showCategories, setShowCategories] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [buyerInfo, setBuyerInfo] = useState(null);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false); // New state for profile dropdown
  const navigate = useNavigate();

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const Buyer_ID = localStorage.getItem('Buyer_ID');
        if (Buyer_ID) {
          const response = await axios.get('http://localhost:3000/auth/buyerinfo', {
            withCredentials: true,
          });
          setBuyerInfo(response.data);
          setIsLoggedIn(true);
        }
      } catch (error) {
        console.error('Error fetching buyer info:', error);
        setIsLoggedIn(false);
      }
    };
    checkLoginStatus();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('Buyer_ID'); // Clear the buyer ID from local storage
    setIsLoggedIn(false);
    setBuyerInfo(null);
    setShowProfileDropdown(false); // Hide profile dropdown on logout
    navigate('/login'); // Redirect to login page after logout
  };

  const handleProfileClick = () => {
    setShowProfileDropdown(!showProfileDropdown); // Toggle the profile dropdown
  };

  const handleCategoriesClick = () => {
    setShowCategories(!showCategories); // Toggle the categories visibility
  };

  return (
    <>
      {/* Header Component */}
      <div className="bg-white">
        <div className="border py-3 px-6">
          <div className="flex justify-between">
            <div className="flex items-center py-0 px-1">
              <Link to="/">
                <img
                  src={logo}
                  alt="Kacha Bazar Logo"
                  style={{ width: "110px", height: "50px", padding: "0" }}
                />
              </Link>
            </div>
            <div className="ml-6 flex flex-1 gap-3">
              <input
                type="text"
                className="w-full rounded-md border border-[#DDE2E4] px-3 py-2 text-sm"
                defaultValue="Search On Kaca Bazar"
              />
            </div>
            <div className="flex gap-x-2">
              <div
                className="flex cursor-pointer items-center gap-x-1 rounded-md py-2 px-4 hover:bg-gray-100"
                onClick={() => (window.location.href = "/farmerlogin")}
              >
                <img
                  src={farmer}
                  alt="farmer"
                  style={{ width: "30px", height: "30px", padding: "0" }}
                />
                <span className="text-sm font-medium">Become Farmer</span>
              </div>

              <div
                className="flex cursor-pointer items-center gap-x-1 rounded-md py-2 px-4 hover:bg-gray-100"
                onClick={() => (window.location.href = "/sellerlogin")}
              >
                <img
                  src={seller}
                  alt="seller"
                  style={{ width: "30px", height: "30px", padding: "0" }}
                />
                <span className="text-sm font-medium">Become Seller</span>
              </div>

              <div className="flex cursor-pointer items-center gap-x-1 rounded-md py-2 px-4 hover:bg-gray-100">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-5 w-5 text-gray-500"
                  viewBox="0 0 20 20"
                  fill="currentColor"
                >
                  <path d="M4 3a2 2 0 100 4h12a2 2 0 100-4H4z" />
                  <path
                    fillRule="evenodd"
                    d="M3 8h14v7a2 2 0 01-2 2H5a2 2 0 01-2-2V8zm5 3a1 1 0 011-1h2a1 1 0 110 2H9a1 1 0 01-1-1z"
                    clipRule="evenodd"
                  />
                </svg>
                <span className="text-sm font-medium">Orders</span>
              </div>

              <div className="flex cursor-pointer items-center gap-x-1 rounded-md py-2 px-4 hover:bg-gray-100">
                <div className="relative">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    className="h-5 w-5 text-gray-500"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                  >
                    <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
                  </svg>
                  <span className="absolute -top-2 -right-2 flex h-4 w-4 items-center justify-center rounded-full bg-red-500 p-2 text-xs text-white">
                    3
                  </span>
                </div>
                <span className="text-sm font-medium">Cart</span>
              </div>

              {/* Conditional rendering of Login or Profile icon */}
              {isLoggedIn ? (
                <div style={{ position: 'relative' }}>
                  <img
                    src="path-to-profile-icon.png"
                    alt="Profile"
                    style={{ width: '32px', height: '32px', cursor: 'pointer' }}
                    onClick={handleProfileClick} // Toggle dropdown visibility
                  />
                  {showProfileDropdown && buyerInfo && ( // Show dropdown if it's visible and buyer info is available
                    <div style={{
                      display: 'flex',
                      flexDirection: 'column',
                      backgroundColor: 'white',
                      boxShadow: '0px 8px 16px rgba(0, 0, 0, 0.2)',
                      position: 'absolute',
                      top: '100%',
                      right: '0',
                      padding: '10px',
                      minWidth: '160px',
                      zIndex: '1',
                    }}>
                      <p style={{ margin: '0', padding: '5px 0' }}>{buyerInfo.Buyer_Name}</p>
                      <p style={{ margin: '0', padding: '5px 0' }}>{buyerInfo.Email}</p>
                      <p style={{ margin: '0', padding: '5px 0' }}>{buyerInfo.Phone_Number}</p>
                      <p style={{ margin: '0', padding: '5px 0' }}>{buyerInfo.Address}</p>
                      <button onClick={handleLogout} style={{
                        marginTop: '10px',
                        padding: '8px',
                        backgroundColor: '#dc3545',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                      }}>
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <Link to="/login" style={{
                  padding: '8px 16px',
                  backgroundColor: '#007bff',
                  color: '#fff',
                  borderRadius: '4px',
                  textDecoration: 'none',
                }}>Login</Link>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
