import "../styles/layouts/navbar.css";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useState } from "react";
import { DebounceInput } from "react-debounce-input";
import MobileMenu from "./MobileMenu"; // Ensure you have this component

const Navbar = ({ setSearchQuery }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const [mobileMenuStatus, setMobileMenuStatus] = useState(false);

  const logoutHandler = () => {
    // Add your logout logic here
  };

  return (
    <nav className="align-center navbar">
      <div className="mobile-header">
        <i
          className="material-icons mobile-menu-icon"
          onClick={() => setMobileMenuStatus((prev) => !prev)}
        >
          menu
        </i>
        <div className="nav-mobile-brand fs-md">
          <Link to="/">
            Agro<span className="dark-brown-color">Stores</span>
          </Link>
        </div>
      </div>
      <div className="nav-brand fs-lg">
        <Link to="/">
          Agro<span className="dark-brown-color">Stores</span>
        </Link>
      </div>

      <div className="search-field">
        <i className="material-icons" id="search-icon">
          search
        </i>
        <DebounceInput
          minLength={2}
          debounceTimeout={500}
          type="text"
          name="search-bar"
          id="search-bar"
          placeholder="Search"
          onClick={() => navigate("/search")}
          onChange={(e) => setSearchQuery(e.target.value)}
          autoFocus={location.pathname === "/search"}
        />
      </div>

      <ul className="nav-pills align-center fw-bold">
        <li>
          <button className="btn btn-solid" onClick={logoutHandler}>
            Logout
          </button>
        </li>
        <li>
          <Link to="/wishlist">
            <i className="material-icons mg-left-sm">favorite</i>
            <span className="badge-count">0</span> {/* Placeholder for wishlist count */}
          </Link>
        </li>
        <li>
          <Link to="/cart">
            <i className="material-icons mg-left-sm" id="shop-cart">
              shopping_cart
            </i>
            <span className="badge-count">0</span> {/* Placeholder for cart count */}
          </Link>
        </li>
        <li>
          <Link to="/profile">
            <i className="material-icons mg-left-sm" id="shop-cart">
              account_circle
            </i>
          </Link>
        </li>
      </ul>
      <MobileMenu
        mobileMenuStatus={mobileMenuStatus}
        setMobileMenuStatus={setMobileMenuStatus}
        logoutHandler={logoutHandler}
        wishlistItemsCount={0} // Placeholder
        cartItemsCount={0} // Placeholder
      />
    </nav>
  );
};

export default Navbar;
