import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import SellerHeader from "../component/SellerHeader";
import Footer from "../component/Footer";

function BuyFromFarmersPage() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch products from farmers (Replace with your API endpoint)
    fetch("http://localhost:3000/api/farmer-products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error(err));
  }, []);

  const handleBuyProduct = (productId) => {
    // Implement buy logic (e.g., API call to place order)
    alert(`Buying product with ID: ${productId}`);
  };

  return (
    <>
      <SellerHeader />
      <div style={{ padding: "20px", maxWidth: "1200px", margin: "auto" }}>
        <h1
          className="font-bold text-4xl"
          style={{ textAlign: "center", marginBottom: "20px", color: "green" }}
        >
          Buy Products from Farmers
        </h1>
        <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
          {products.length > 0 ? (
            products.map((product) => (
              <div
                key={product.id}
                style={{
                  border: "1px solid green",
                  borderRadius: "10px",
                  padding: "20px",
                  width: "30%",
                  textAlign: "center",
                }}
              >
                <h3 style={{ color: "green" }}>{product.name}</h3>
                <p>{product.description}</p>
                <p>Price: ${product.price}</p>
                <button
                  style={{
                    backgroundColor: "green",
                    color: "white",
                    padding: "10px 20px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                  onClick={() => handleBuyProduct(product.id)}
                >
                  Buy Now
                </button>
              </div>
            ))
          ) : (
            <p style={{ textAlign: "center", width: "100%" }}>
              No products available at the moment.
            </p>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}

export default BuyFromFarmersPage;
