import React from 'react';
import { BrowserRouter as Router, Route, Routes, NavLink } from 'react-router-dom';

function FAQs() {
  const faqData = {
    promotions: [
      { question: "What are the current promotions?", answer: "We offer discounts and bundle offers regularly." },
      { question: "How can I apply a discount?", answer: "You can apply discount codes at checkout." },
    ],
    accountManagement: [
      { question: "How do I reset my password?", answer: "Go to account settings and click 'Reset Password'." },
    ],
    orders: [
      { question: "How do I track my order?", answer: "You can track your order in the 'My Orders' section." },
    ],
    payments: [
      { question: "How do I make a payment?", answer: "You can make a payment using credit cards, bank transfers, or mobile payment methods." },
    ],
  };

  const FAQSidebar = () => (
    <div style={{ width: '250px', backgroundColor: '#f5f5f5', padding: '10px' }}>
      <ul style={{ listStyleType: 'none', padding: '0' }}>
        {Object.keys(faqData).map((category, index) => (
          <li key={index} style={{ marginBottom: '10px' }}>
            <NavLink
              to={`/faqs/${category}`}
              style={({ isActive }) => ({
                textDecoration: 'none',
                color: isActive ? 'white' : '#333',
                display: 'block',
                padding: '10px',
                backgroundColor: isActive ? '#007bff' : '#fff',
                border: '1px solid #ddd',
                borderRadius: '5px',
              })}
            >
              {category.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
            </NavLink>
          </li>
        ))}
      </ul>
    </div>
  );

  const FAQContent = ({ category }) => (
    <div style={{ flex: 1, padding: '20px' }}>
      <h2 style={{ marginBottom: '20px' }}>{category.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</h2>
      {faqData[category]?.map((faq, index) => (
        <div key={index} style={{ marginBottom: '20px' }}>
          <h3 style={{ marginBottom: '10px' }}>{faq.question}</h3>
          <p>{faq.answer}</p>
        </div>
      ))}
    </div>
  );

  return (
    <Router>
      <div style={{ display: 'flex', padding: '20px', maxWidth: '1200px', margin: 'auto' }}>
        <FAQSidebar />
        <Routes>
          {Object.keys(faqData).map((category, index) => (
            <Route
              key={index}
              path={`/faqs/${category}`}
              element={<FAQContent category={category} />}
            />
          ))}
          {/* Default Route */}
          <Route
            path="/"
            element={<div style={{ flex: 1, padding: '20px' }}>Please select a FAQ category from the sidebar.</div>}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default FAQs;
