import React, { useState, useEffect } from "react";
import axios from "axios";

function FarmerPreviousAd() {
  const [ads, setAds] = useState([]);
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState("success");

  const farmertoken = localStorage.getItem("farmertoken");

  // Fetch farmer's ads
  useEffect(() => {
    const fetchAds = async () => {
      try {
        const response = await axios.get("http://localhost:3000/farmer/ads", {
          headers: {
            Authorization: `Bearer ${farmertoken}`,
          },
        });
        setAds(response.data);
      } catch (error) {
        console.error("Error fetching ads:", error);
        setMessage("Failed to load ads.");
        setMessageType("error");
      }
    };

    fetchAds();
  }, [farmertoken]);

  // Handle ad deletion
  const handleDelete = async (adId) => {
    try {
      const response = await axios.delete(`http://localhost:3000/farmer/ad/${adId}`, {
        headers: {
          Authorization: `Bearer ${farmertoken}`,
        },
      });

      if (response.data && response.data.success) {
        setMessage("Ad deleted successfully.");
        setMessageType("success");
        setAds(ads.filter((ad) => ad.id !== adId)); // Remove the deleted ad from the list
      } else {
        setMessage("Failed to delete ad.");
        setMessageType("error");
      }
    } catch (error) {
      setMessage("An error occurred while deleting the ad.");
      setMessageType("error");
    }

    // Clear the message after 3 seconds
    setTimeout(() => setMessage(""), 3000);
  };

  return (
    <div>
      <h2 style={{ color: "green" }}>Your Previous Ads</h2>

      {message && (
        <p
          style={{
            color: messageType === "success" ? "green" : "red",
            fontWeight: "bold",
          }}
        >
          {message}
        </p>
      )}

      {ads.length > 0 ? (
        <ul>
          {ads.map((ad) => (
            <li key={ad.id} style={{ marginBottom: "20px", border: "1px solid #ccc", padding: "10px" }}>
              <p><strong>Product:</strong> {ad.Product_Name}</p>
              <p><strong>Unit Name:</strong> {ad.Unit_Name}</p>
              <p><strong>Minimum Order Quantity:</strong> {ad.Minimum_Order_Quantity}</p>
              <p><strong>Unit Price:</strong> {ad.Unit_Price}</p>
              <p><strong>Delivery Date:</strong> {ad.Delivery_Date}</p>
              <p><strong>Description:</strong> {ad.Description}</p>
              <p><strong>Phone Number:</strong> {ad.Phone_Number}</p>
              <p><strong>Picture URL:</strong> {ad.Picture_URL}</p>
              
              <button
                style={{ backgroundColor: "red", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" }}
                onClick={() => handleDelete(ad.id)}
              >
                Delete Ad
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No previous ads found.</p>
      )}
    </div>
  );
}

export default FarmerPreviousAd;
