import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import logo from "../images/KacaBazarLogo.png";
import farmer from "../images/farmerlogo.png";
import profile from "../images/profile.png";
import axios from 'axios';

export default function FarmerHeader() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [farmerInfo, setfarmerInfo] = useState(null);
  const navigate = useNavigate();
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const farmertoken = localStorage.getItem('farmertoken');
        if (farmertoken) {
          const response = await axios.get('http://localhost:3000/auth/farmerInfo', {
            headers: { Authorization: `Bearer ${farmertoken}` },
          });
          setfarmerInfo(response.data);
          setIsLoggedIn(true);
        }
      } catch (error) {
        console.error('Error fetching farmer info:', error);
        setIsLoggedIn(false);
      }
    };
    checkLoginStatus();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('farmertoken');
    setIsLoggedIn(false);
    setfarmerInfo(null);
    navigate('/');
  };

  const handleProfileClick = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  return (
    <div className="bg-white border-b py-3 px-4 md:px-6">
      <div className="flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center">
          <Link to="/farmer">
            <img
              src={logo}
              alt="Kaca Bazar Logo"
              className="w-28 md:w-36 h-auto"
            />
          </Link>
        </div>

        {/* Search Bar */}
        <div className="flex flex-1 max-w-md mx-2 md:max-w-xs lg:max-w-md">
          <input
            type="text"
            className="w-full rounded-md border border-[#DDE2E4] px-3 py-2 text-sm"
            placeholder="Search on Kaca Bazar"
          />
        </div>

        {/* Links and Profile */}
        <div className="flex items-center gap-4">
          {/* Update Profile Link */}
          <div
            className="flex items-center gap-x-2 cursor-pointer py-2 px-4 hover:bg-gray-100"
            onClick={() => (window.location.href = "/farmer/update-profile")}
          >
            <img
              src={farmer}
              alt="Farmer"
              className="w-8 h-8"
            />
            <span className="hidden md:block text-sm font-medium">Update Profile</span>
          </div>

          {/* Orders Link */}
          <div
            className="flex items-center gap-x-2 cursor-pointer py-2 px-4 hover:bg-gray-100"
            onClick={() => (window.location.href = "/farmer/orders")}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-5 w-5 text-gray-500"
              viewBox="0 0 20 20"
              fill="currentColor"
            >
              <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
            </svg>
            <span className="text-sm font-medium">Ad Product</span>
          </div>

          {/* Profile and Logout */}
          {isLoggedIn ? (
            <div className="relative">
              <img
                src={profile}
                alt="Profile"
                className="w-9 h-9 cursor-pointer"
                onClick={handleProfileClick}
              />
              {showProfileDropdown && farmerInfo && (
                <div className="absolute right-0 top-full mt-2 w-56 bg-[#b5f8f1] p-4 shadow-md z-10 rounded-md">
                  <p className="text-lg font-medium text-[#0010ad]">Name: {farmerInfo.Farmer_Name}</p>
                  <p className="text-sm text-[#0010ad]">Email: {farmerInfo.Email}</p>
                  <p className="text-sm text-[#0010ad]">Phone: {farmerInfo.Phone_Number}</p>
                  <p className="text-sm text-[#0010ad]">Location: {farmerInfo.Location_Name}</p>
                  <div className="flex gap-2 mt-3">
                    <button
                      onClick={() => (window.location.href = "/farmer/update-profile")}
                      className="px-4 py-2 bg-[#009e48] text-white rounded-md"
                    >
                      Update Profile
                    </button>
                    <button
                      onClick={handleLogout}
                      className="px-4 py-2 bg-[#009e48] text-white rounded-md"
                    >
                      Logout
                    </button>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <Link
              to="/login"
              className="px-4 py-2 bg-[#007bff] text-white rounded-md text-sm"
            >
              Login
            </Link>
          )}
        </div>
      </div>
    </div>
  );
}
