import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, NavLink } from 'react-router-dom';
import Header from '../component/Header';
import Footer from '../component/Footer';

function FAQSidebar() {
  return (
    <div style={{ width: '250px', backgroundColor: '#f5f5f5', padding: '10px' }}>
      <ul style={{ listStyleType: 'none', padding: '0' }}>
        <li style={{ marginBottom: '10px' }}>
          <NavLink
            to="/faqs/promotions"
            style={({ isActive }) => ({
              textDecoration: 'none',
              color: isActive ? 'white' : '#333',
              display: 'block',
              padding: '10px',
              backgroundColor: isActive ? '#007bff' : '#fff',
              border: '1px solid #ddd',
              borderRadius: '5px'
            })}
          >
            Promotions
          </NavLink>
        </li>
        <li style={{ marginBottom: '10px' }}>
          <NavLink
            to="/faqs/account-management"
            style={({ isActive }) => ({
              textDecoration: 'none',
              color: isActive ? 'white' : '#333',
              display: 'block',
              padding: '10px',
              backgroundColor: isActive ? '#007bff' : '#fff',
              border: '1px solid #ddd',
              borderRadius: '5px'
            })}
          >
            Account Management
          </NavLink>
        </li>
        <li style={{ marginBottom: '10px' }}>
          <NavLink
            to="/faqs/orders"
            style={({ isActive }) => ({
              textDecoration: 'none',
              color: isActive ? 'white' : '#333',
              display: 'block',
              padding: '10px',
              backgroundColor: isActive ? '#007bff' : '#fff',
              border: '1px solid #ddd',
              borderRadius: '5px'
            })}
          >
            Orders
          </NavLink>
        </li>
        <li style={{ marginBottom: '10px' }}>
          <NavLink
            to="/faqs/payments"
            style={({ isActive }) => ({
              textDecoration: 'none',
              color: isActive ? 'white' : '#333',
              display: 'block',
              padding: '10px',
              backgroundColor: isActive ? '#007bff' : '#fff',
              border: '1px solid #ddd',
              borderRadius: '5px'
            })}
          >
            Payments
          </NavLink>
        </li>
      </ul>
    </div>
  );
}

function FAQContent({ category, faqs }) {
  return (
    <div style={{ flex: 1, padding: '20px' }}>
      <h2 style={{ marginBottom: '20px' }}>{category}</h2>
      {faqs.map((faq, index) => (
        <div key={index} style={{ marginBottom: '20px' }}>
          <h3 style={{ marginBottom: '10px' }}>{faq.question}</h3>
          <p>{faq.answer}</p>
        </div>
      ))}
    </div>
  );
}

const faqData = {
  promotions: [
    { question: "What are the current promotions?", answer: "We offer discounts and bundle offers regularly." },
    { question: "How can I apply a discount?", answer: "You can apply discount codes at checkout." },
  ],
  accountManagement: [
    { question: "How do I reset my password?", answer: "Go to account settings and click 'Reset Password'." },
  ],
  orders: [
    { question: "How do I track my order?", answer: "You can track your order in the 'My Orders' section." },
  ],
  // Add more categories and FAQs here
};

function FAQs() {
  return (
    <>
    </>
    
  );
}

export default FAQs;
