import { useState, useEffect } from "react";
import logo from "../images/KacaBazarLogo.png";
import { Link, useNavigate } from "react-router-dom";
import seller from "../images/agent.png";
import farmer from "../images/farmerlogo.png";
import profile from "../images/profile.png";
import axios from 'axios';

export default function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [buyerInfo, setBuyerInfo] = useState(null);
  const navigate = useNavigate();
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const buyertoken = localStorage.getItem('buyertoken');
        if (buyertoken) {
          const response = await axios.get('http://localhost:3000/auth/buyerinfo', {
            headers: { Authorization: `Bearer ${buyertoken}` },
          });
          setBuyerInfo(response.data);
          setIsLoggedIn(true);
        }
      } catch (error) {
        console.error('Error fetching buyer info:', error);
        setIsLoggedIn(false);
      }
    };
    checkLoginStatus();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('buyertoken');
    console.log("Log out Successfully!");
    setIsLoggedIn(false);
    setBuyerInfo(null);
    navigate('/');
  };

  const handleProfileClick = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  return (
    <div style={{ backgroundColor: 'white' }}>
      <div style={{ borderBottom: '1px solid #DDE2E4', padding: '1rem 1.5rem' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Link to="/" style={{ display: 'flex', alignItems: 'center' }}>
            <img
              src={logo}
              alt="Kacha Bazar Logo"
              style={{ width: '110px', height: '50px', padding: '0' }}
            />
          </Link>
          
          {/* Search bar - hidden on small screens */}
          <div
            style={{
              flex: '1',
              marginLeft: '1.5rem',
              display: 'flex',
              gap: '0.75rem',
              maxWidth: '100%',
              display: window.innerWidth < 640 ? 'none' : 'flex' 
            }}
          >
            <input
              type="text"
              style={{
                width: '100%',
                borderRadius: '4px',
                border: '1px solid #DDE2E4',
                padding: '0.5rem 0.75rem',
                fontSize: '0.875rem'
              }}
              placeholder="Search On Kaca Bazar"
            />
          </div>

          <div style={{ display: 'flex', gap: '0.5rem' }}>
            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.25rem',
                cursor: 'pointer',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                hoverBackgroundColor: '#F8F9FA'
              }}
              onClick={() => (window.location.href = "/farmerlogin")}
            >
              <img src={farmer} alt="farmer" style={{ width: '30px', height: '30px' }} />
              <span style={{ fontSize: '0.875rem', fontWeight: '500' }}>Become Farmer</span>
            </div>

            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.25rem',
                cursor: 'pointer',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                hoverBackgroundColor: '#F8F9FA'
              }}
              onClick={() => (window.location.href = "/sellerlogin")}
            >
              <img src={seller} alt="seller" style={{ width: '30px', height: '30px' }} />
              <span style={{ fontSize: '0.875rem', fontWeight: '500' }}>Become Seller</span>
            </div>

            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.25rem',
                cursor: 'pointer',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                hoverBackgroundColor: '#F8F9FA'
              }}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                style={{ width: '20px', height: '20px', color: '#6B7280' }}
                viewBox="0 0 20 20"
                fill="currentColor"
              >
                <path d="M4 3a2 2 0 100 4h12a2 2 0 100-4H4z" />
                <path
                  fillRule="evenodd"
                  d="M3 8h14v7a2 2 0 01-2 2H5a2 2 0 01-2-2V8zm5 3a1 1 0 011-1h2a1 1 0 110 2H9a1 1 0 01-1-1z"
                  clipRule="evenodd"
                />
              </svg>
              <span style={{ fontSize: '0.875rem', fontWeight: '500' }}>Orders</span>
            </div>

            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.25rem',
                cursor: 'pointer',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                hoverBackgroundColor: '#F8F9FA'
              }}
            >
              <div style={{ position: 'relative' }}>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  style={{ width: '20px', height: '20px', color: '#6B7280' }}
                  viewBox="0 0 20 20"
                  fill="currentColor"
                >
                  <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
                </svg>
                <span style={{
                  position: 'absolute',
                  top: '-0.25rem',
                  right: '-0.25rem',
                  backgroundColor: '#EF4444',
                  color: 'white',
                  fontSize: '0.625rem',
                  borderRadius: '9999px',
                  padding: '0.25rem'
                }}>
                  3
                </span>
              </div>
              <span style={{ fontSize: '0.875rem', fontWeight: '500' }}>Cart</span>
            </div>

            {isLoggedIn ? (
              <div style={{ position: 'relative' }}>
                <img
                  src={profile}
                  alt="Profile"
                  style={{ width: '35px', height: '35px', cursor: 'pointer' }}
                  onClick={handleProfileClick}
                />
                {showProfileDropdown && buyerInfo && (
                  <div style={{
                    position: 'absolute',
                    top: '100%',
                    right: '0',
                    backgroundColor: '#b5f8f1',
                    boxShadow: '0px 8px 16px rgba(0, 0, 0, 0.2)',
                    padding: '10px',
                    zIndex: '1',
                    minWidth: '260px'
                  }}>
                    <p style={{ margin: '0', padding: '5px 0' }}>Name: {buyerInfo.Buyer_Name}</p>
                    <p style={{ margin: '0', padding: '5px 0' }}>Email: {buyerInfo.Email}</p>
                    <button onClick={handleLogout} style={{
                      marginTop: '10px',
                      padding: '4px',
                      backgroundColor: '#dc3545',
                      color: '#fff',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}>
                      Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <Link to="/login" style={{
                fontSize: '0.875rem',
                fontWeight: '500',
                color: '#000000',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                textDecoration: 'none'
              }}>
                Login
              </Link>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
