import React, { useState } from "react";
import axios from "axios";
import eggImage from "../images/egg.png";
import fishImage from "../images/fish.png";
import chickenImage from "../images/chicken.png";
import fruitImage from "../images/fruit.png";
import vegitableImage from "../images/vegitable.png";
import riceImage from "../images/rice.png";
import meatImage from "../images/meat.png";
import spiceImage from "../images/spice.png";

const ProductCategories = () => {
  const [ads, setAds] = useState([]);
  const [message, setMessage] = useState(""); // New state for message

  const handleClick = async (category) => {
    try {
      const response = await axios.get(`http://localhost:3000/buyer/product-ads?category=${category}`);
      
      if (response.data.length === 0) {
        setMessage("No products available for this category.");
      } else {
        setMessage(""); // Clear message if ads are available
      }

      setAds(response.data);
    } catch (error) {
      console.error("Error fetching product ads:", error);
      setMessage("Error loading products. Please try again later.");
    }
  };

  return (
    <section className="py-12 bg-white sm:py-16 lg:py-20">
      <div className="px-4 mx-auto sm:px-6 lg:px-8 max-w-7xl">
        <div className="max-w-md mx-auto text-center">
          <h2 className="text-2xl font-bold text-gray-900 sm:text-3xl">
            Our Product Categories
          </h2>
          <p className="mt-4 text-base font-normal leading-7 text-gray-600">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Purus
            faucibus massa dignissim tempus.
          </p>
        </div>

        <div className="grid grid-cols-2 gap-6 mt-10 lg:mt-16 lg:gap-4 lg:grid-cols-4">
          {/* Egg */}
          <div className="relative group cursor-pointer" onClick={() => handleClick("Egg")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={eggImage} alt="Egg" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Egg</h3>
          </div>
          
          {/* Fish */}
          <div className="relative group cursor-pointer" onClick={() => handleClick("Fish")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={fishImage} alt="Fish" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Fish</h3>
          </div>
          
          {/* Chicken */}
          <div className="relative group cursor-pointer" onClick={() => handleClick("Chicken")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={chickenImage} alt="Chicken" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Chicken</h3>
          </div>

          {/* Other categories similar to the ones above */}
          <div className="relative group cursor-pointer" onClick={() => handleClick("Vegetables")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={vegitableImage} alt="Vegetables" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Vegetables</h3>
          </div>

          <div className="relative group cursor-pointer" onClick={() => handleClick("Fruits")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={fruitImage} alt="Fruits" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Fruits</h3>
          </div>

          <div className="relative group cursor-pointer" onClick={() => handleClick("Rice")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={riceImage} alt="Rice" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Rice</h3>
          </div>

          <div className="relative group cursor-pointer" onClick={() => handleClick("Meat")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={meatImage} alt="Meat" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Meat</h3>
          </div>

          <div className="relative group cursor-pointer" onClick={() => handleClick("Spice")}>
            <div className="overflow-hidden">
              <img className="object-cover w-64 h-64 transition-all duration-300 group-hover:scale-125" src={spiceImage} alt="Spice" />
            </div>
            <h3 className="text-lg font-bold text-gray-900 sm:text-xl md:text-2xl text-center mt-4">Spice</h3>
          </div>
        </div>
      </div>

      {/* Message Display */}
      {message && <p className="text-center text-red-500 font-semibold mt-6">{message}</p>}

      {/* Product Ads Display */}
      <div className="product-ads mt-10">
        {ads.map((ad) => (
          <div key={ad.Product_AD_ID} className="ad-item p-4 border-b border-gray-200">
            <h3 className="text-lg font-semibold">{ad.Product_Name}</h3>
            <p>Price: ${ad.Unit_Price} per {ad.Unit_Name}</p>
            <p>Stock: {ad.Stock}</p>
            <p>Minimum Order: {ad.Minimum_Order_Quantity}</p>
            <p>Delivery Date: {ad.Delivery_Date}</p>
            <p>{ad.Description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProductCategories;
