import React, { useState } from "react";
// import logo from "../images/KacaBazarLogo.png";
import gif from "../images/logo.gif";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "../component/Header";
import Footer from "../component/Footer";

function SellerLogin() {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const handleChanges = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const navigate = useNavigate();

  const handleSumbit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:3000/auth/login",
        values
      );
      console.log(response);
      if (response.status === 201) {
        localStorage.setItem("token", response.data.token);
        navigate("/");
      }
    } catch (err) {
      console.log(err.message);
    }
  };

  return (
    <>
      <Header />
      <div className="flex-1  items-center p-4">
        <div className="flex justify-center items-center p-4" style={{ backgroundColor: 'green' }}>
            <div>
                kjndf
            </div>
          {/* Set the container div width larger on medium and larger screens */}
          <div className="shadow-lg px-8 py-5 border w-full max-w-lg md:max-w-3xl border-radius-5" style={{ backgroundColor: 'white' }}>
            <div className="flex flex-col md:flex-row justify-center items-center">
              {/* On smaller screens, image is placed above the form */}
              <img
                style={{ width: "300px", height: "auto" }}
                src={gif}
                alt="Animated GIF"
                className="mb-4 md:mb-0 md:mr-4 w-full md:w-1/2"
              />
              <div className="flex justify-center items-center flex-col w-full md:w-1/2">
                <h2 className="text-lg font-bold mb-4">Login</h2>
                <form className="w-full" onSubmit={handleSumbit}>
                  <div className="mb-4">
                    <label htmlFor="email" className="block text-gray-700">
                      Email
                    </label>
                    <input
                      type="email"
                      placeholder="Enter Email"
                      className="w-full px-3 py-2 border"
                      name="email"
                      onChange={handleChanges}
                    />
                  </div>
                  <div className="mb-4">
                    <label htmlFor="password" className="block text-gray-700">
                      Password
                    </label>
                    <input
                      type="password"
                      placeholder="Enter Password"
                      className="w-full px-3 py-2 border"
                      name="password"
                      onChange={handleChanges}
                    />
                  </div>
                  <button className="w-full bg-green-600 text-white py-2">
                    Submit
                  </button>
                </form>
                <div className="text-center mt-4">
                  <span>Don't have an seller account?</span>
                  <Link to="/register" className="text-blue-500">
                    Sign Up
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </>
  );
}

export default SellerLogin;
